import React, { Component } from 'react'
import Addressbar from './Addressbar'
import Express from '../abis/Express'
import Web3 from 'web3'

class ParcelSender extends Component {
  state = {
    recvData: {},
    account:0,
    totalNumber:0,
    startTime: 0,
    endTime: 0,
    orderWeight: 0,
    orderType: 0,
    orderPrice: 0,
  }
  async componentDidMount() {
    await this.getWeb3Provider()
    await this.connectToBlockchain()

  }

  handleChange1 = e => {
    //  select startTime
    this.setState({
      startTime: e.target.value,
    })
  }
  handleChange2 = e => {
    // select endTime
    this.setState({
      endTime: e.target.value,
    })
  }
  handleChange3 = e => {
    // select orderWeight
    this.setState({
      orderWeight: e.target.value,
    })
    if (e.target.value == 1)
      this.setState({
        orderPrice: 3,
      })

    if (e.target.value == 2)
      this.setState({
        orderPrice: 5,
      })
    if (e.target.value == 3)
      this.setState({
        orderPrice: 7,
      })
  }
  handleChange4 = e => {
    // select orderType
    this.setState({
      orderType: e.target.value,
    })
  }

  async getWeb3Provider() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    } else {
      window.alert(
        'Non-Ethereum browser detected. You should consider trying MetaMask!',
      )
    }
  }

  async connectToBlockchain() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Express.networks[networkId]
    if (networkData) {
      const deployedExpress = new web3.eth.Contract(
        Express.abi,
        networkData.address,
      )
      this.setState({ deployedExpress: deployedExpress })
      const totalNumber = await deployedExpress.methods.totalNumber().call()
      console.log(totalNumber)
      this.setState({ totalNumber })
      for (var i = 1;i<= totalNumber;i++) {
        const order1 = await deployedExpress.methods.order1(i).call();
        const order2 = await deployedExpress.methods.order2(i).call();
        const order3= await deployedExpress.methods.order3(i).call();
        const orderAll = [...order1, ...order2, ...order3];
        this.setState({
          orders:[...this.state.orders, orderAll]
        });
      }
      console.log("myOrder", this.state.orders)

    } else {
      window.alert('Express contract is not found in your blockchain.')
    }
  }

  createOrder1 = async (_senderName, _senderPhone, _pickupAddr,_shippingAddr,_receiverName, _receiverPhone) => {
    this.setState ({loading: true})
    const gasAmount = await this.state.deployedExpress.methods.createOrder1(_senderName, _senderPhone, _pickupAddr,_shippingAddr,_receiverName, _receiverPhone).estimateGas({from: this.state.account})
    this.state.deployedExpress.methods.createOrder1(_senderName, _senderPhone, _pickupAddr,_shippingAddr,_receiverName, _receiverPhone).send({from: this.state.account, gas: gasAmount})
    .once('receipt', (receipt)=> {
      this.setState({loading: false});
    })
  }

  createOrder2 = async (_startTime, _endTime, _orderWeight, _orderType, _receiver, _orderPrice) => {
    this.setState ({loading: true}) 
    const gasAmount = await this.state.deployedExpress.methods.createOrder2(_startTime, _endTime, _orderWeight, _orderType, _receiver).estimateGas({from: this.state.account, value: _orderPrice})
    this.state.deployedExpress.methods.createOrder2(_startTime, _endTime, _orderWeight, _orderType, _receiver).send({from: this.state.account, value: _orderPrice, gas: gasAmount})
    .once('receipt', (receipt)=> {
      this.setState({loading: false});
    })
  }
  takeOrder = async (_orderId, _currentTime) => {
    this.setState ({loading: true})
    const gasAmount = await this.state.deployedExpress.methods.takeOrder(_orderId).estimateGas({from: this.state.account})
    this.state.deployedExpress.methods.takeOrder(_orderId).send({from: this.state.account, gas: gasAmount })
    .once('receipt', (receipt)=> {
      this.setState({loading: false});
    })
  }

  render() {
    return (
      <div>
        <Addressbar account={this.state.account} />
        <div className="container-fluid mt-5">
          <div id="createOrder">
            <h2>You Can Create Your Order Now!</h2>
            <form onSubmit = {async (event) => {
              event.preventDefault();

              const senderName = this.senderName.value;
              const senderPhone = this.senderPhone.value;
              const pickupAddr = this.pickupAddr.value;
              const shippingAddr = this.shippingAddr.value;
              const receiverName = this.receiverName.value;
              const receiverPhone = this.receiverPhone.value;
              const startTime = this.state.startTime
              const endTime = this.state.endTime
              const orderWeight = this.state.orderWeight
              const orderType = this.state.orderType
             // const orderPrice = window.web3.utils.toWei(this.state.orderPrice.toString(), 'Ether')   
             const orderPrice = this.state.orderPrice
              const receiverAddr = this.receiverAddr


              await this.createOrder1(senderName, senderPhone, pickupAddr,shippingAddr,receiverName, receiverPhone)
              await this.createOrder2(startTime, endTime, orderWeight, orderType, receiverAddr,orderPrice)
            }
            }>
              <div className="form-group mr-sm-2">
                <input
                  id="senderName"
                  type="text"
                  ref={input => {
                    this.senderName = input
                  }}
                  className="form-control"
                  placeholder="Sender Name"
                  required
                />
              </div>

              <div className="form-group mr-sm-2">
                <input
                  id="senderPhone"
                  type="Number"
                  ref={input => {
                    this.senderPhone = input
                  }}
                  className="form-control"
                  placeholder="Sender Phone"
                  required
                />
              </div>
              <div className="form-group mr-sm-2">
                <input
                  id="pickupAddr"
                  type="text"
                  ref={input => {
                    this.pickupAddr = input
                  }}
                  className="form-control"
                  placeholder="Pick Up Address"
                  required
                />
              </div>
              <div className="form-group mr-sm-2">
                <input
                  id="shippingAddr"
                  type="text"
                  ref={input => {
                    this.shippingAddr = input
                  }}
                  className="form-control"
                  placeholder="Shipping Address"
                  required
                />
              </div>
              <div className="form-group mr-sm-2">
                <input
                  id="receiverName"
                  type="text"
                  ref={input => {
                    this.receiverName = input
                  }}
                  className="form-control"
                  placeholder="Receiver Name"
                  required
                />
              </div>

              <div className="form-group mr-sm-2">
                <input
                  id="receiverPhone"
                  type="Number"
                  ref={input => {
                    this.receiverPhone = input
                  }}
                  className="form-control"
                  placeholder="Receiver Phone"
                  required
                />
              </div>

              <div className="form-group mr-sm-2">
                <select onChange={this.handleChange1}>
                  <option value="0">Please select your pick up time</option>
                  <option value="0">12am</option>
                  <option value="1">1am</option>
                  <option value="2">2am</option>
                  <option value="3">3am</option>
                  <option value="4">4am</option>
                  <option value="5">5am</option>
                  <option value="6">6am</option>
                  <option value="7">7am</option>
                  <option value="8">8am</option>
                  <option value="9">9am</option>
                  <option value="10">10am</option>
                  <option value="11">11am</option>
                  <option value="12">12pm</option>
                  <option value="13">1pm</option>
                  <option value="14">2pm</option>
                  <option value="15">3pm</option>
                  <option value="16">4pm</option>
                  <option value="17">5pm</option>
                  <option value="18">6pm</option>
                  <option value="19">7pm</option>
                  <option value="20">8pm</option>
                  <option value="21">9pm</option>
                  <option value="22">10pm</option>
                  <option value="23">11pm</option>
                  <option value="24">12pm</option>
                </select>

                <select onChange={this.handleChange2}>
                  <option value="0">
                    Please select your expected receive time
                  </option>
                  <option value="0">12am</option>
                  <option value="1">1am</option>
                  <option value="2">2am</option>
                  <option value="3">3am</option>
                  <option value="4">4am</option>
                  <option value="5">5am</option>
                  <option value="6">6am</option>
                  <option value="7">7am</option>
                  <option value="8">8am</option>
                  <option value="9">9am</option>
                  <option value="10">10am</option>
                  <option value="11">11am</option>
                  <option value="12">12pm</option>
                  <option value="13">1pm</option>
                  <option value="14">2pm</option>
                  <option value="15">3pm</option>
                  <option value="16">4pm</option>
                  <option value="17">5pm</option>
                  <option value="18">6pm</option>
                  <option value="19">7pm</option>
                  <option value="20">8pm</option>
                  <option value="21">9pm</option>
                  <option value="22">10pm</option>
                  <option value="23">11pm</option>
                  <option value="24">12pm</option>
                </select>
              </div>

              <div className="form-group mr-sm-2">
                <select onChange={this.handleChange3}>
                  <option value="0">Please select your package's weight</option>
                  <option value="1">0-10kg</option>
                  <option value="2">10-20kg</option>
                  <option value="3">20-30kg</option>
                </select>

                <select onChange={this.handleChange4}>
                  <option value="0">Please select your package's type</option>
                  <option value="Commodity">Commodity</option>
                  <option value="Food">Food</option>
                  <option value="Document">Document</option>
                  <option value="Digital Products">Digital Products</option>
                  <option value="Clothing">Clothing</option>
                  <option value="Others">Others</option>
                </select>
              </div>
              <div className="form-group mr-sm-2">
              <input
                id="receiverAddr"
                type="text"
                ref={input => {
                  this.receiverAddr = input
                }}
                className="form-control"
                placeholder="Receiver Hash Address"
                required
              />
            </div>
              <h2>You Order Price: {this.state.orderPrice} ether</h2>

              <button type="submit" className="btn btn-primary">
                Create Order
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default ParcelSender
