import React, { Component } from 'react'
import Addressbar from './Addressbar'

class ParcelSender extends Component {
  state = {
    recvData: {},
    startTime: 0,
    endTime: 0,
    orderWeight: 0,
    orderType: 0,
    orderPrice: 0,
  }
  async componentDidMount() {
    let recvParam
    if (this.props.location && this.props.location.query) {
      //To see if parameters exist
      recvParam = this.props.location.query
      sessionStorage.setItem('recvData', JSON.stringify(recvParam)) // store recvParam in sessionStorage
    } else {
      recvParam = JSON.parse(sessionStorage.getItem('recvData'))
    }
    this.setState({
      recvData: recvParam,
    })
    console.log('recvParam', recvParam)
    console.log('recvData', this.state.recvData);
  }

  handleChange1 = e => {
    //  select startTime
    this.setState({
      startTime: e.target.value,
    })
  }
  handleChange2 = e => {
    // select endTime
    this.setState({
      endTime: e.target.value,
    })
  }
  handleChange3 = e => {
    // select orderWeight
    this.setState({
      orderWeight: e.target.value,
    })
    if (e.target.value == 1)
      this.setState({
        orderPrice: 3,
      })

    if (e.target.value == 2)
      this.setState({
        orderPrice: 5,
      })
    if (e.target.value == 3)
      this.setState({
        orderPrice: 7,
      })
  }
  handleChange4 = e => {
    // select orderType
    this.setState({
      orderType: e.target.value,
    })
  }

  render() {
    return (
      <div>
        <Addressbar account={this.state.recvData.account} />
        <div className="container-fluid mt-5">
          <div id="createOrder">
            <h2>You Can Create Your Order Now!</h2>
            <form onSubmit = {async (event) => {
              event.preventDefault();

              const senderName = this.senderName.value;
              const senderPhone = this.senderPhone.value;
              const pickupAddr = this.pickupAddr.value;
              const shippingAddr = this.shippingAddr.value;
              const receiverName = this.receiverName.value;
              const receiverPhone = this.receiverPhone.value;
              const startTime = this.state.startTime
              const endTime = this.state.endTime
              const orderWeight = this.state.orderWeight
              const orderType = this.state.orderType
             // const orderPrice = window.web3.utils.toWei(this.state.orderPrice.toString(), 'Ether')   
             const orderPrice = this.state.orderPrice
              const receiverAddr = this.receiverAddr


              await this.state.recvData.createOrder1(senderName, senderPhone, pickupAddr,shippingAddr,receiverName, receiverPhone)
              await this.state.recvData.createOrder2(startTime, endTime, orderWeight, orderType, receiverAddr,orderPrice)
            }
            }>
              <div className="form-group mr-sm-2">
                <input
                  id="senderName"
                  type="text"
                  ref={input => {
                    this.senderName = input
                  }}
