import React, { Component } from 'react'
import Addressbar from './Addressbar'

class ParcelSender extends Component {
  state = {
    recvData: {},
    startTime: 0,
    endTime: 0,
    orderWeight: 0,
    orderType: 0,
    orderPrice: 0,
  }
  async componentDidMount() {
    let recvParam
    if (this.props.location && this.props.location.query) {
      //To see if parameters exist
      recvParam = this.props.location.query
      sessionStorage.setItem('recvData', JSON.stringify(recvParam)) // store recvParam in sessionStorage
    } else {
      recvParam = JSON.parse(sessionStorage.getItem('recvData'))
    }
    this.setState({
      recvData: recvParam,
    })
    console.log('recvParam', recvParam)
  }

  handleChange1 = (e)  => { //  select startTime
    this.setState({
        startTime: e.target.value
    })
  }
  handleChange2  = (e)  =>  { // select endTime
    this.setState({
        endTime: e.target.value
    })
  }
  handleChange3  = (e)  =>  { // select orderWeight
    this.setState({
        orderWeight: e.target.value
    })
    if(e.target.value==1)
    this.setState({
      orderPrice: 3
  })

  if(e.target.value==2)
    this.setState({
      orderPrice: 5
  })
  if(e.target.value==3)
    this.setState({
      orderPrice: 7
  })


  }
  handleChange4  = (e)  =>  { // select orderType
    this.setState({
        orderType: e.target.value
    })
  }

  render() {
    return (
      <div>
        <Addressbar account={this.state.recvData.account} />
        <div className="container-fluid mt-5">
          <div id="createOrder">
            <h2>You Can Create Your Order Now!</h2>
            <form
              onSubmit={async event => {

              }}
            >
              <div className="form-group mr-sm-2">
                <input
                  id="senderName"
                  type="text"
                  ref={input => {
                    this.senderName = input
                  }}
                  className="form-control"
                  placeholder="Sender Name"
                  required
                />
              </div>

              <div className="form-group mr-sm-2">
                <input
                  id="senderPhone"
                  type="Number"
                  ref={input => {
                    this.senderPhone = input
                  }}
                  className="form-control"
                  placeholder="Sender Phone"
                  required
                />
              </div>
              <div className="form-group mr-sm-2">
                <input
                  id="pickUpAddr"
                  type="text"
                  ref={input => {
                    this.pickUpAddr = input
                  }}
                  className="form-control"
                  placeholder="Pick Up Address"
                  required
                />
              </div>
              <div className="form-group mr-sm-2">
                <input
                  id="shippingAddr"
                  type="text"
                  ref={input => {
                    this.shippingAddr = input
                  }}
                  className="form-control"
                  placeholder="Shipping Address"
                  required
                />
              </div>
              <div className="form-group mr-sm-2">
                <input
                  id="receiverName"
                  type="text"
                  ref={input => {
                    this.receiverName = input
                  }}
                  className="form-control"
                  placeholder="Receiver Name"
                  required
                />
              </div>

              <div className="form-group mr-sm-2">
                <input
                  id="receiverPhone"
                  type="Number"
                  ref={input => {
                    this.receiverPhone = input
                  }}
                  className="form-control"
                  placeholder="Receiver Phone"
                  required
                />
              </div>
              <div className="form-group mr-sm-2">
              <select onChange={this.handleChange1}>
                        <option value="0">Please select your pick up time</option>
                        <option value="apple"></option>
                        <option value="banana">banana</option>
                        <option value="pear">pear</option>
                        <option value="orange">orange</option>
              </select>

              <select onChange={this.handleChange2}>
                        <option value="未选择">请选择</option>
                        <option value="apple">apple</option>
                        <option value="banana">banana</option>
                        <option value="pear">pear</option>
                        <option value="orange">orange</option>
              </select>
              </div>
              <div className="form-group mr-sm-2">
              <select onChange={this.handleChange3}>
                        <option value="未选择">请选择</option>
                        <option value="1">apple</option>
                        <option value="banana">banana</option>
                        <option value="pear">pear</option>
                        <option value="orange">orange</option>
              </select>
              <select onChange={this.handleChange4}>
                        <option value="未选择">Please select your parcel type</option>
                        <option value="apple">apple</option>
                        <option value="banana">banana</option>
                        <option value="pear">pear</option>
                        <option value="orange">orange</option>
              </select>
              </div>



              <div className="form-group mr-sm-2">
                <input
                  id="orderWeight"
                  type="Number"
                  ref={input => {
                    this.orderWeight = input
                  }}
                  onChange={this.caculatePrice}
                  className="form-control"
                  placeholder="Order Weight"
                  required
                />
              </div>
              <div className="form-group mr-sm-2">
              <h2>You Order Price: {this.state.orderPrice} ether</h2> 
              </div>
              <button type="submit" className="btn btn-primary">
                Add Item
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default ParcelSender
